import { Component } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";

@Component({
  selector: 'app-gateway-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'client';

  credentials = {username: '', password: ''};

  authenticated = true;

  constructor(private http: HttpClient) {
  }

  login() {
    const headers = new HttpHeaders(this.credentials ? {
      authorization : 'Basic ' + btoa(this.credentials.username + ':' + this.credentials.password)
    } : {});

    this.http.get('/user', {headers: headers}).subscribe( {
      next: response => {
        this.authenticated = response.hasOwnProperty('name');
      },
      error: error => {
        console.log(error.toString());
      }
    });
  }
}
<div class="container" [hidden]="authenticated">
  <form role="form" (submit)="login()">
    <div class="form-group">
      <label for="username">Username:</label> <input type="text"
                                                     class="form-control" id="username" name="username"
                                                     [(ngModel)]="credentials.username" />
    </div>
    <div class="form-group">
      <label for="password">Password:</label> <input type="password"
                                                     class="form-control" id="password" name="password"
                                                     [(ngModel)]="credentials.password" />
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
<div class="container" [hidden]="!authenticated">
<!--  todo-->
  <a class="btn btn-primary" href="/diatop/">Go To User Interface</a>
</div>
